css:
table {
        font-size: 12px;
    }
    .invoice-table-div table {
        min-height: 300px;
    }
    .invoice-table-div table tr:last-child td {
        height: 100%;
    }
    .footer {
    position: fixed;
    bottom: 20px;
    left: 0;
    right: 0;
}
    .footer-space {
        height: 150px;
    }
    h4.invoice-title {
        text-align: center;
        font-size: 20px;
        margin-bottom: 20px;
        margin-top: 0;
    }
    @media print {
        table td div {
            page-break-inside: auto !important;
        }

        table {
            page-break-after: auto !important;
        }

        tr {
            page-break-inside: avoid;
            page-break-after: auto;
        }

        td {
            page-break-inside: avoid;
            page-break-after: auto;
        }

        th {
            page-break-inside: avoid;
            page-break-after: auto;
        }

        #thead {
            display: table-header-group;
        }

        tfoot {
            display: table-footer-group;
        }

    }
    .pr-0 {
        padding-right:0!important;
    }
    .pl-0 {
        padding-left:0!important;
    }
